import React, { useEffect, useState } from 'react';
import { Typography } from '@mui/material';
import fetchModel from '../../lib/fetchModelData';
import { useParams } from 'react-router-dom';

function UserDetail() {
  const { userId } = useParams();
  const [user, setUser] = useState(null);
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    fetchModel(`/user/${userId}`)
      .then((data) => setUser(data))
      .catch((error) => console.error('Error fetching user data:', error));

    fetchModel(`/photosOfUser/${userId}`)
      .then((data) => setPhotos(data))
      .catch((error) => console.error('Error fetching photos:', error));
  }, [userId]);

  if (!user) {
    return <Typography>Loading or user not found...</Typography>;
  }

  return (
    <div style={{ padding: 16 }}>
      <Typography variant="h5">
        {user.first_name} {user.last_name}
      </Typography>
      <Typography>Location: {user.location}</Typography>
      <Typography>Description: {user.description}</Typography>
      <Typography>Occupation: {user.occupation}</Typography>
      <Typography variant="h6" style={{ marginTop: 16 }}>Photos:</Typography>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8 }}>
        {(photos || []).map((photo) => (
          <img
            key={photo._id}
            src={`/images/${photo.file_name}`}
            alt={photo.file_name}
            style={{ width: 150, height: 150, objectFit: 'cover', borderRadius: 8 }}
          />
        ))}
      </div>
    </div>
  );
}

export default UserDetail;
